INSERT INTO `query` VALUES (2, '员工', 'staff', 1, 10, 0, 0, 0, 1, 0, 'SELECT \r\n   A.Id,\r\n   A.OpenId,\r\n   A.Name,\r\n   A.Phone,\r\n   A.CreateTime,\r\n	(select SUM(Money) from etc_client where StaffId=a.Id) AllMoney, \r\n	(select SUM(Money) from etc_client where StaffId=a.Id AND Status=\'已结算\') PaidMoney,\r\n	(select SUM(Money) from etc_client where StaffId=a.Id AND Status!=\'已结算\') NoPaidMoney,\r\n	(select count(1) from etc_client where StaffId=a.Id) MonthAllNum,\r\n	(select count(1) from etc_client where StaffId=a.Id) AllNum,\r\n   A.Status\r\nFROM etc_staff A', '{\r\n    \"Id\": {\r\n        \"title\": \"Id\",\r\n        \"type\": \"text\"\r\n    },\r\n    \"OpenId\": {\r\n        \"title\": \"OpenId\",\r\n        \"type\": \"text\"\r\n    },\r\n    \"Name\": {\r\n        \"title\": \"推广员姓名\",\r\n        \"type\": \"text\"\r\n    },\r\n    \"Phone\": {\r\n        \"title\": \"推广员手机号\",\r\n        \"type\": \"text\"\r\n    },\r\n    \"CreateTime\": {\r\n        \"title\": \"加入时间\",\r\n        \"type\": \"datetimebox\"\r\n    },\r\n    \"AllMoney\": {\r\n        \"title\": \"总收益\",\r\n        \"type\": \"numberbox\"\r\n    },\r\n    \"PaidMoney\": {\r\n        \"title\": \"已结算\",\r\n        \"type\": \"numberbox\"\r\n    },\r\n    \"NoPaidMoney\": {\r\n        \"title\": \"待结算\",\r\n        \"type\": \"numberbox\"\r\n    },\r\n    \"MonthAllNum\": {\r\n        \"title\": \"本月推广单数\",\r\n        \"type\": \"numberbox\"\r\n    },\r\n    \"AllNum\": {\r\n        \"title\": \"推广总单数\",\r\n        \"type\": \"numberbox\"\r\n    },\r\n    \"Status\": {\r\n        \"title\": \"账号状态\",\r\n        \"type\": \"text\"\r\n    }\r\n}', NULL, NULL, '[\r\n   {\r\n        \"title\": \"查看详情\",\r\n        \"dialogMode\": \"Look\"\r\n    }\r\n]', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
